&lt;h2 id=&#34;实例&#34;&gt;实例&lt;/h2&gt;
&lt;p&gt;下面给出一些&lt;a href=&#34;https://echarts.apache.org/examples/zh/index.html&#34;&gt;echarts官方实例&lt;/a&gt;，大多数都可以&lt;strong&gt;交互&lt;/strong&gt;。&lt;/p&gt;
&lt;h3 id=&#34;折线图Line&#34;&gt;折线图Line&lt;/h3&gt;
&lt;h4 id=&#34;Stacked-area-chart&#34;&gt;&lt;a href=&#34;https://echarts.apache.org/examples/zh/editor.html?c=area-stack&#34;&gt;Stacked area chart&lt;/a&gt;&lt;/h4&gt;
&lt;div id=&#34;echarts9025&#34; style=&#34;width: 85%;height: 400px;margin: 0 auto&#34;&gt;&lt;/div&gt;
&lt;script type=&#34;text/javascript&#34;&gt;
        // 基于准备好的dom，初始化echarts实例
        var myChart = echarts.init(document.getElementById(&#39;echarts9025&#39;));

        // 指定图表的配置项和数据
        var option = option = {
    title: {
        text: &#39;堆叠区域图&#39;
    },
    tooltip: {
        trigger: &#39;axis&#39;,
        axisPointer: {
            type: &#39;cross&#39;,
            label: {
                backgroundColor: &#39;#6a7985&#39;
            }
        }
    },
    legend: {
        data: [&#39;邮件营销&#39;, &#39;联盟广告&#39;, &#39;视频广告&#39;, &#39;直接访问&#39;, &#39;搜索引擎&#39;]
    },
    toolbox: {
        feature: {
            saveAsImage: {}
        }
    },
    grid: {
        left: &#39;3%&#39;,
        right: &#39;4%&#39;,
        bottom: &#39;3%&#39;,
        containLabel: true
    },
    xAxis: [
        {
            type: &#39;category&#39;,
            boundaryGap: false,
            data: [&#39;周一&#39;, &#39;周二&#39;, &#39;周三&#39;, &#39;周四&#39;, &#39;周五&#39;, &#39;周六&#39;, &#39;周日&#39;]
        }
    ],
    yAxis: [
        {
            type: &#39;value&#39;
        }
    ],
    series: [
        {
            name: &#39;邮件营销&#39;,
            type: &#39;line&#39;,
            stack: &#39;总量&#39;,
            areaStyle: {},
            data: [120, 132, 101, 134, 90, 230, 210]
        },
        {
            name: &#39;联盟广告&#39;,
            type: &#39;line&#39;,
            stack: &#39;总量&#39;,
            areaStyle: {},
            data: [220, 182, 191, 234, 290, 330, 310]
        },
        {
            name: &#39;视频广告&#39;,
            type: &#39;line&#39;,
            stack: &#39;总量&#39;,
            areaStyle: {},
            data: [150, 232, 201, 154, 190, 330, 410]
        },
        {
            name: &#39;直接访问&#39;,
            type: &#39;line&#39;,
            stack: &#39;总量&#39;,
            areaStyle: {},
            data: [320, 332, 301, 334, 390, 330, 320]
        },
        {
            name: &#39;搜索引擎&#39;,
            type: &#39;line&#39;,
            stack: &#39;总量&#39;,
            label: {
                normal: {
                    show: true,
                    position: &#39;top&#39;
                }
            },
            areaStyle: {},
            data: [820, 932, 901, 934, 1290, 1330, 1320]
        }
    ]
};

        // 使用刚指定的配置项和数据显示图表。
        myChart.setOption(option);
&lt;/script&gt;
&lt;h4 id=&#34;Area-Pieces&#34;&gt;&lt;a href=&#34;https://echarts.apache.org/examples/zh/editor.html?c=area-pieces&#34;&gt;Area Pieces&lt;/a&gt;&lt;/h4&gt;
&lt;div id=&#34;echarts4437&#34; style=&#34;width: 85%;height: 400px;margin: 0 auto&#34;&gt;&lt;/div&gt;
&lt;script type=&#34;text/javascript&#34;&gt;
