        var myChart = echarts.init(document.getElementById(&#39;echarts4437&#39;));

        // 指定图表的配置项和数据
        var option = option = {
    xAxis: {
        type: &#39;category&#39;,
        boundaryGap: false
    },
    yAxis: {
        type: &#39;value&#39;,
        boundaryGap: [0, &#39;30%&#39;]
    },
    visualMap: {
        type: &#39;piecewise&#39;,
        show: false,
        dimension: 0,
        seriesIndex: 0,
        pieces: [{
            gt: 1,
            lt: 3,
            color: &#39;rgba(0, 180, 0, 0.5)&#39;
        }, {
            gt: 5,
            lt: 7,
            color: &#39;rgba(0, 180, 0, 0.5)&#39;
        }]
    },
    series: [
        {
            type: &#39;line&#39;,
            smooth: 0.6,
            symbol: &#39;none&#39;,
            lineStyle: {
                color: &#39;green&#39;,
                width: 5
            },
            markLine: {
                symbol: [&#39;none&#39;, &#39;none&#39;],
                label: {show: false},
                data: [
                    {xAxis: 1},
                    {xAxis: 3},
                    {xAxis: 5},
                    {xAxis: 7}
                ]
            },
            areaStyle: {},
            data: [
                [&#39;2019-10-10&#39;, 200],
                [&#39;2019-10-11&#39;, 400],
                [&#39;2019-10-12&#39;, 650],
                [&#39;2019-10-13&#39;, 500],
                [&#39;2019-10-14&#39;, 250],
                [&#39;2019-10-15&#39;, 300],
                [&#39;2019-10-16&#39;, 450],
                [&#39;2019-10-17&#39;, 300],
                [&#39;2019-10-18&#39;, 100]
            ]
        }
    ]
};

        // 使用刚指定的配置项和数据显示图表。
        myChart.setOption(option);
