&lt;h3 id=&#34;日历坐标系Calendar&#34;&gt;日历坐标系Calendar&lt;/h3&gt;
&lt;h4 id=&#34;Calendar-Heatmap-Horizontal&#34;&gt;&lt;a href=&#34;https://echarts.apache.org/examples/zh/editor.html?c=calendar-horizontal&#34;&gt;Calendar Heatmap Horizontal&lt;/a&gt;&lt;/h4&gt;
&lt;script&gt;
function getVirtulData(year) {
    year = year || &#39;2017&#39;;
    var date = +echarts.number.parseDate(year + &#39;-01-01&#39;);
    var end = +echarts.number.parseDate((+year + 1) + &#39;-01-01&#39;);
    var dayTime = 3600 * 24 * 1000;
    var data = [];
    for (var time = date; time &lt; end; time += dayTime) {
        data.push([
            echarts.format.formatTime(&#39;yyyy-MM-dd&#39;, time),
            Math.floor(Math.random() * 1000)
        ]);
    }
    return data;
}
&lt;/script&gt;
&lt;div id=&#34;echarts6393&#34; style=&#34;width: 85%;height: 400px;margin: 0 auto&#34;&gt;&lt;/div&gt;
&lt;script type=&#34;text/javascript&#34;&gt;
        // 基于准备好的dom，初始化echarts实例
        var myChart = echarts.init(document.getElementById(&#39;echarts6393&#39;));

        // 指定图表的配置项和数据
        var option = option = {
    tooltip: {
        position: &#39;top&#39;
    },
    visualMap: {
        min: 0,
        max: 1000,
        calculable: true,
        orient: &#39;horizontal&#39;,
        left: &#39;center&#39;,
        top: &#39;top&#39;
    },

    calendar: [{
        range: &#39;2020&#39;,
        cellSize: [&#39;auto&#39;, 15]
    },
    {
        top: 260,
        range: &#39;2019&#39;,
        cellSize: [&#39;auto&#39;, 15]
    }],

    series: [{
        type: &#39;heatmap&#39;,
        coordinateSystem: &#39;calendar&#39;,
        calendarIndex: 0,
        data: getVirtulData(2020)
    }, {
        type: &#39;heatmap&#39;,
        coordinateSystem: &#39;calendar&#39;,
        calendarIndex: 1,
        data: getVirtulData(2019)
    }]
};

        // 使用刚指定的配置项和数据显示图表。
        myChart.setOption(option);
&lt;/script&gt;
