                }
            ]
        },
        {
            &#34;source&#34;: &#34;/medias/(.*)&#34;,
            &#34;headers&#34;: [
                {
                    &#34;key&#34;: &#34;Cache-Control&#34;,
                    &#34;value&#34;: &#34;public, s-maxage=2592000, max-age=2592000&#34;
                }
            ]
        },
        {
            &#34;source&#34;: &#34;/medias_webp/(.*)&#34;,
            &#34;headers&#34;: [
                {
                    &#34;key&#34;: &#34;Cache-Control&#34;,
