&lt;span id=&#34;more&#34;&gt;&lt;/span&gt;

&lt;div class=&#34;markmap-container&#34; style=&#34;height:950px&#34;&gt;
  &lt;svg data=&#34;{&amp;quot;t&amp;quot;:&amp;quot;heading&amp;quot;,&amp;quot;d&amp;quot;:1,&amp;quot;p&amp;quot;:{&amp;quot;lines&amp;quot;:[1,2]},&amp;quot;v&amp;quot;:&amp;quot;竞品分析&amp;quot;,&amp;quot;c&amp;quot;:[{&amp;quot;t&amp;quot;:&amp;quot;list_item&amp;quot;,&amp;quot;d&amp;quot;:3,&amp;quot;p&amp;quot;:{&amp;quot;lines&amp;quot;:[3,4]},&amp;quot;v&amp;quot;:&amp;quot;一、为什么做竞品分析？&amp;quot;,&amp;quot;c&amp;quot;:[{&amp;quot;t&amp;quot;:&amp;quot;list_item&amp;quot;,&amp;quot;d&amp;quot;:5,&amp;quot;p&amp;quot;:{&amp;quot;lines&amp;quot;:[4,5]},&amp;quot;v&amp;quot;:&amp;quot;1.知己知彼百战百胜&amp;quot;},{&amp;quot;t&amp;quot;:&amp;quot;list_item&amp;quot;,&amp;quot;d&amp;quot;:5,&amp;quot;p&amp;quot;:{&amp;quot;lines&amp;quot;:[5,6]},&amp;quot;v&amp;quot;:&amp;quot;2.为自身的产品设计提供功能、可用性、关键技术等方面的参考&amp;quot;},{&amp;quot;t&amp;quot;:&amp;quot;list_item&amp;quot;,&amp;quot;d&amp;quot;:5,&amp;quot;p&amp;quot;:{&amp;quot;lines&amp;quot;:[6,7]},&amp;quot;v&amp;quot;:&amp;quot;3.提高自身产品的差异化程度&amp;quot;},{&amp;quot;t&amp;quot;:&amp;quot;list_item&amp;quot;,&amp;quot;d&amp;quot;:5,&amp;quot;p&amp;quot;:{&amp;quot;lines&amp;quot;:[7,8]},&amp;quot;v&amp;quot;:&amp;quot;4.降低风险&amp;quot;}]},{&amp;quot;t&amp;quot;:&amp;quot;list_item&amp;quot;,&amp;quot;d&amp;quot;:3,&amp;quot;p&amp;quot;:{&amp;quot;lines&amp;quot;:[8,9]},&amp;quot;v&amp;quot;:&amp;quot;二、如何选择竞品？&amp;quot;,&amp;quot;c&amp;quot;:[{&amp;quot;t&amp;quot;:&amp;quot;list_item&amp;quot;,&amp;quot;d&amp;quot;:5,&amp;quot;p&amp;quot;:{&amp;quot;lines&amp;quot;:[9,10]},&amp;quot;v&amp;quot;:&amp;quot;1.寻找行业内领先的榜样产品（取其精华）&amp;quot;},{&amp;quot;t&amp;quot;:&amp;quot;list_item&amp;quot;,&amp;quot;d&amp;quot;:5,&amp;quot;p&amp;quot;:{&amp;quot;lines&amp;quot;:[10,11]},&amp;quot;v&amp;quot;:&amp;quot;2.类似于自己的产品（竞争关系最大）&amp;quot;},{&amp;quot;t&amp;quot;:&amp;quot;list_item&amp;quot;,&amp;quot;d&amp;quot;:5,&amp;quot;p&amp;quot;:{&amp;quot;lines&amp;quot;:[11,12]},&amp;quot;v&amp;quot;:&amp;quot;3.有潜力的竞品（存活原因）&amp;quot;}]},{&